using CrashKonijn.Agent.Core;
using CrashKonijn.Goap.Runtime;
using CrashKonijn.Goap.Core;
using UnityEngine;

namespace Assets.Scripts.GOAP.Sensors
{
    [GoapId("DifficultySensor-e5f6a7b8-c9d0-4e1f-2a3b-4c5d6e7f8a9b")]
    public class DifficultySensor : LocalWorldSensorBase
    {
        public override void Created() { }
        public override void Update() { }

        public override SenseValue Sense(IActionReceiver agent, IComponentReference refs)
        {
            // Get difficulty as a percentage (0-100)
            float difficulty = Assets.Scripts.DDA.DifficultyTracker.GetDifficultyF() * 100f;
            return new SenseValue(Mathf.RoundToInt(difficulty));
        }
    }
}

using CrashKonijn.Goap.Runtime;

namespace Assets.Scripts.GOAP.WorldKeys
{
    [GoapId("Difficulty-c3d4e5f6-a7b8-4c9d-0e1f-2a3b4c5d6e7f")]
    public class Difficulty : WorldKeyBase {}
}
